sudo nano /etc/nginx/nginx.conf

user creio users;
worker_processes  1;
#error_log  logs/error.log;
#error_log  logs/error.log  notice;
#error_log  logs/error.log  info;
#pid        logs/nginx.pid;
events {
    worker_connections  1024;
}

http {
    include       mime.types;
    default_type  application/octet-stream;
    #access_log  logs/access.log  main;
    types_hash_max_size 4096;
    server_names_hash_bucket_size 128;
    sendfile        on;
    #tcp_nopush     on;
    keepalive_timeout  65;
    gzip  on;

    server {
        listen 80 default_server;
        listen [::]:80 default_server;

        root /usr/share/nginx/html;
        index index.htm index.html;

        server_tokens off;
        autoindex off;
    }

    include sites-enabled/*;
}

# cat /etc/nginx/sites-available/home.conf
server {
  listen 4000;
  listen [::]:4000;
  # server_name creio.duckdns.org;

  root /media/files/github/ctlos/ctlos.github.io/_site;
  index index.html index.htm index.php;

  access_log /var/log/nginx/home.access;
  error_log /var/log/nginx/home.error;

  location / {
      autoindex off;
      try_files $uri $uri/ =404;
    }

    error_page 404 /404.html;
    location = /404.html {
        internal;
    }

    if ($request_uri = /index.html) {
        return 301 /;
    }
}

cat /etc/nginx/sites-enabled/home.ctlos.ru.conf
# содержимое
server {
    listen 80;
    listen [::]:80;
    server_name creio.duckdns.org;

    access_log /var/log/nginx/home.access;
    error_log /var/log/nginx/home.error;

    # reverse proxy
    location / {
        proxy_pass http://10.0.0.10:4000;
        # proxy_redirect http:// $scheme://;
        proxy_redirect off;
        proxy_buffering off;
        tcp_nodelay on;

        proxy_http_version 1.1;
        proxy_pass_header Server;
        proxy_set_header Host $http_host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;
        proxy_set_header Proxy "Cloud-LB";
        proxy_set_header Upgrade $http_upgrade;
    }
}
